import React, { useState } from 'react';

const New=()=>{

    let [Num,Update]=useState(0);
    const Incre=()=>{
        Update(Num+1)
    }

    const Decre=()=>{
        
        Num > 0 ? Update(Num - 1) :alert('Sorry, Zero Limit reached')

        // if (Num>0) {
        //     Update(Num-1);
        // }
        // else{
        //     Update(0);
        //     alert('Sorry, Zero Limit reached')
        // }
         
    }
    return(
        <>
            <div>
                <h1>{Num}</h1>
                <button onClick={Incre}>Increment</button>
                <button onClick={Decre}>Decrement</button>
            </div>
        </>
    )
}

export default New;
