  flexDirection:'column',
  marginTop: '-30px',
}
let h1={
  display: 'flex',
  justifyContent: 'center',
  padding: '1rem',
  borderRadius: '10px',
  background: 'white'
}

ReactDOM.render(<>
  <div style={newStyle}>
    <h1 style={h1}><span style={{color:'#fa9191'}}>{start}</span><span style={{color:'green'}}>{greeting}</span></h1>
