import React, { useState } from 'react';

const UpdateTime=()=>{

    let state=useState();

    let [curTime,update]= useState(new Date().toLocaleTimeString())

    const GetTime=()=>{
        update(curTime == (new Date().toLocaleTimeString()));
    };
    return(
        <div style={{ display: 'flex', flexDirection: 'column', justifyContent: 'center', alignItems: 'center' }}>
                <h3>{curTime}</h3>
                <button onClick={GetTime} style={{ padding: '0.5rem', color: 'white', backgroundColor: 'purple', outline: 'none', borderRadius: '10px', border: '0px', margin: '-15px 5px 16px 0' }}>GetTime</button>
        </div>         
    )
}

export default UpdateTime;
