import React, { useState } from 'react';


const Event=()=>{

    let Name='Click Me'
    let purple='#8e44ad';
    let [curBg,updateBg]= useState(purple);
    let [NewName,updateName]= useState(Name);
    
    
    const ChangeBG=()=>{
        let Uname='Ouch!! ðŸ˜œ';
        updateName(Uname);
       let newcolor='#4d474d';
        updateBg(newcolor);
    }
    return(
        <>
        <div style={{backgroundColor: curBg , display: 'flex', flexDirection: 'column', justifyContent: 'center', alignItems: 'center' }}>
        {/* <button onClick={ChangeBG}style={{ padding: '0.5rem', color: 'white', backgroundColor: 'purple', outline: 'none', borderRadius: '10px', border: '0px', margin: '10px 5px 10px 0' }}>{NewName}</button> */}
        <button onDoubleClick={ChangeBG}style={{ padding: '0.5rem', color: 'white', backgroundColor: 'purple', outline: 'none', borderRadius: '10px', border: '0px', margin: '10px 5px 10px 0' }}>{NewName}</button>
        </div>
        </>
    )
}

export default Event;
