import React, { useState } from 'react';

const IncNumber = () => {
    // const state = useState();
    let [count, update] = useState(0);

    const IncNum = () => {
        
        update(count + 1);
    };

    const DecNum=()=>{
        if(count < 0){
            alert('Decrement limit reached')
            
        }
        
    }

    return (
        <>

            <div style={{ display: 'flex', flexDirection: 'column', justifyContent: 'center', alignItems: 'center' }}>
                <h3>{count}</h3>
                <div>
                    <button onClick={IncNum} style={{ padding: '0.5rem', color: 'white', backgroundColor: 'purple', outline: 'none', borderRadius: '10px', border: '0px', margin: '-15px 5px 16px 0' }}>INCREASE</button>
                    <button onClick={DecNum} style={{padding:'0.5rem',color:'white',backgroundColor:'purple',outline:'none',borderRadius:'10px',border:'0px',margin: '-15px 0 16px 0'}}>DECREASE</button>
                </div></div>
        </>)
}

export default IncNumber;
