import React,{ useState } from 'react';

const Card=(props)=>{

    let [Ans,Update]=useState();
    const show=()=>{
        Update(props.Ans);
    }

    const hide=()=>{
        Update(' ');
    }
    return(
        <>
        <div>
            <div ><span  style={{cursor:'pointer'}} onClick={show}>{hide ? '+' : '-'}</span><span style={{cursor:'pointer'}} ></span>{props.Que}</div><br />
            <div>{Ans}</div>
        </div>
        </>
    )
}

export default Card;
